import React, { useEffect, useState } from "react";
import ItemList from "../ItemList/ItemList";
import { getFirestore } from "../../firebase";
import { Link } from "react-router-dom";

export default function ItemListContainer({tipo = null}) {
	const [products, setproducts] = useState([]);
	const db = getFirestore();

	useEffect(() => {
		const GetItems = async () => {
			if (!tipo) {
				try {
					let arr = [];
					const pregunta = await db.collection("items").get();
					pregunta.forEach((doc) => {
						arr.push({ id: doc.id, data: doc.data() });
					});
					setproducts(arr);
				} catch (error) {
					console.log(error);
				}
			}else{
				try {
					console.log(tipo);
					let arr = [];
					const pregunta = await db.collection("items").where('Monitores', '==',tipo)
					.get();
					pregunta.forEach((doc) => {
						arr.push({ id: doc.id, data: doc.data() });
					});
					setproducts(arr);
				} catch (error) {
					console.log(error);
				}
			}

		};
		GetItems();
	}, [db]);

	return (
		<div>
			<div className="container mt-4 d-flex justify-content-center">
			<Link to="/discossd" className="btn btn-dark ms-2">
				Discos SSD
			</Link>
			<Link to="/monitores" className="btn btn-dark ms-3">
				Monitores
			</Link>
			<Link to="/otros" className="btn btn-dark ms-3">
				Otros
			</Link>
			</div>

			<ItemList products={products} />
		</div>
	);
}
