import React from "react";
import styled from "styled-components";
import ItemCount from "../ItemCount/ItemCount";
import {useCartContext} from '../../context/context';
import { AiFillTag, AiFillMediumSquare, AiFillDollarCircle} from "react-icons/ai";

const DIV = styled.div`
	margin-top: 18%;
`;
const ItemDetail = ({ item }) => {

	const {addToCart} = useCartContext();


    const handleAddProduct = (e, qty) => {
        e.stopPropagation();

        addToCart({
            cantidad: qty,
            item,
        }, qty)
    }
	return (
		<div className="container ">
			<div className="row">
				<DIV className="col-lg-6" >
					<img src={item.picture} alt={item.titulo} className="img-fluid" />
				</DIV>
				<div className="col-lg-6 fs-5">
					<h2>{item.nombre}</h2>
					<p>{item.descripcion}</p>					

                        <ul className="list-unstyled"> 

                            <li><AiFillTag/> Marca: {item.marca}</li>
                            <li><AiFillMediumSquare/> Modelo: {item.modelo}</li>
                            <li>✅Ayuda a descansar mejor</li>
                            <li>✅Protección UV</li>
                            <li>✅Reducción de reflejos</li>
                            <li>✅Mayor resistencia a rayas</li>
                        </ul>

					<h4><AiFillMediumSquare/> Precio: ${item.precio}</h4>
					<h5>Stock: {item.stock}</h5>
					{
						item.stock >= 1 ? <ItemCount stock={item.stock} initial={1} onAdd={handleAddProduct}/> : null
					}

				</div>
			</div>
		</div>
	);
};

export default ItemDetail;
