import React from "react";
import { useCartContext } from "../../context/context";
import { Nav, ButonLogIn } from "./NavbarAndWidgetCss";
import  logo  from '../../img/logo.png'
import CartWidget from "./CartWidget";

import { NavLink, Link } from "react-router-dom";

const Navbar = () => {
	const { autenticacion, userName } = useCartContext();

	const handleLogIn = () => {
		autenticacion();
	};

	return (
		<header>
			      <div className="fixed">
        <nav className="menu">
          {/* Logo tienda */}
          <div className="logo">
            <Link to="/">
              <div className="d-flex">   
                <img src={logo} alt="Logo de la empresa" />
				<h4 className="text-danger">Magios-Team</h4>
              </div>
            </Link>
          </div>



          {/* Enlaces */}
          <ul className="enlaces">
            <li>
              <NavLink to="/" activeClassName="active">
                Inicio
              </NavLink>
            </li>
            <li>
			  <NavLink to="/discossd" className="nav-link active">
                Discos SSD
              </NavLink>
            </li>
            <li>
              <NavLink to="/monitores" className="nav-link active">
                Monitores
              </NavLink>
            </li>
            <li>
              <NavLink to="/otros" className="nav-link active">
                Otros
              </NavLink>
            </li>
          </ul>
		  {userName ? (
						<p className="ms-auto mt-3">Bienvenido, {userName}</p>
					) : (
						<ButonLogIn className="btn btn-dark" onClick={handleLogIn}>
							Ingresar</ButonLogIn>
					)}

          {/* Carrito */}
		  <NavLink to="/carrito" className="ms-auto">
						<CartWidget />
		  </NavLink>

        </nav>
      </div>

		</header>
	);
};

export default Navbar;
